svg.script:
const g = document.getElementById("g"),
      p = document.getElementById("p");
let t0 = null, cycle = 900;

// Кастомная кривая Безье с параметрами 0.1,0.4,0.6,0.9
function bezier(t) {
  return 3 * Math.pow(1-t,2) * t * 0.4 +
         3 * (1-t) * Math.pow(t,2) * 0.9 +
         Math.pow(t,3);
}

function rad(d){ return d * Math.PI / 180; }

function polar(cx,cy,r,a){
  return [cx + r * Math.cos(rad(a)), cy + r * Math.sin(rad(a))];
}

function anim(ts) {
  if(!t0) t0 = ts;
  let dt = (ts - t0) % cycle, rot, sw;

  if(dt < 300) {
    let pr = dt / 300;
    rot = -20 + 110 * bezier(pr); // вращение: -20° → 90°
    let arcDur = 200,
        par = Math.min(dt / arcDur, 1);
    sw = 90 * bezier(par);         // дуга: 0° → 90°
  } else if(dt < 350) {
    let pr = (dt - 300) / 50;
    rot = 90 + (180 - 90) * bezier(pr); // 90° → 180°
    sw = 90;
  } else if(dt < 600) {
    let pr = (dt - 350) / 250;
    rot = 180 + 90 * bezier(pr);        // 180° → 270°
    sw = 90;
  } else {
    let pr = (dt - 600) / 300;
    rot = 270 + 90 * bezier(pr);        // 270° → 360°
    sw = 90 * (1 - bezier(pr));         // дуга: 90° → 0°
  }

  const sa = -90, ea = sa + sw,
        [x1,y1] = polar(14,14,12,sa),
        [x2,y2] = polar(14,14,12,ea);

  p.setAttribute("d", sw > 0.01 ?
    `M ${x1} ${y1} A 12 12 0 ${sw > 180 ? 1 : 0} 1 ${x2} ${y2}` :
    `M ${x1} ${y1} L ${x1} ${y1}`);

  g.setAttribute("transform", `rotate(${rot % 360} 14 14)`);
  requestAnimationFrame(anim);
}

requestAnimationFrame(anim);
